/* playnote.com.dao.IMemberdDao.list */ SELECT @rownum := @rownum+1 AS rnum ,UUID AS uuid ,EMAIL AS email ,PASSWORD AS password ,USERNAME AS username ,NICKNAME AS nickname ,GRADE AS grade ,PHONE AS phone ,EMAIL_CERT AS emailCert ,REGISTER_DATETIME AS registerDatetime ,LASTLOGIN_DATETIME AS lastloginDatetime ,LASTLOGIN_IP AS lastloginIp ,IS_ADMIN AS isAdmin ,ADMINMEMO AS adminmemo ,CREATE_DATETIME AS createDatetime ,CREATE_USER AS createUser ,UPDATE_DATETIME AS updateDatetime ,UPDATE_USER AS updateUser FROM MEMBER ,(SELECT @rownum := 0) AS R WHERE 1 = 1
AND UUID = #{UUID}
AND EMAIL = #{EMAIL}
AND PASSWORD = #{PASSWORD}
ORDER BY CREATE_DATETIME DESC

/* playnote.com.dao.IMemberdDao.detail */ SELECT UUID AS uuid ,EMAIL AS email ,PASSWORD AS password ,USERNAME AS username ,NICKNAME AS nickname ,GRADE AS grade ,PHONE AS phone ,EMAIL_CERT AS emailCert ,REGISTER_DATETIME AS registerDatetime ,LASTLOGIN_DATETIME AS lastloginDatetime ,LASTLOGIN_IP AS lastloginIp ,IS_ADMIN AS isAdmin ,ADMINMEMO AS adminmemo ,CREATE_DATETIME AS createDatetime ,CREATE_USER AS createUser ,UPDATE_DATETIME AS updateDatetime ,UPDATE_USER AS updateUser FROM MEMBER WHERE 1 = 1
AND UUID = #{UUID}
AND EMAIL = #{EMAIL}
AND PASSWORD = #{PASSWORD}
