mapper.select:
/* kr.pe.playnote.main.dao.IBoardDao.listDao */ SELECT @rownum := @rownum+1 AS rnum ,UUID AS uuid ,BOARD_CODE AS boardCode ,USER_ID AS userId ,SUBJECT AS subject ,CONTENTS AS contents ,HITS AS hits ,CREATE_DATETIME AS createDatetime ,CREATE_USER AS createUser ,UPDATE_DATETIME AS updateDatetime ,UPDATE_USER AS updateUser FROM BOARD ,(SELECT @rownum := 0) AS R WHERE 1 = 1
AND BOARD_CODE = #{BOARD_CODE}
ORDER BY CREATE_DATETIME DESC LIMIT #{PAGE_LIMIT}
ORDER BY CREATE_DATETIME DESC LIMIT 5

/* kr.pe.playnote.main.dao.IBoardDao.pageListDao */ SELECT BOARD.rnum AS rnum ,BOARD.uuid AS uuid ,BOARD.boardCode AS boardCode ,BOARD.userId AS userId ,BOARD.userName AS userName ,BOARD.email AS email ,BOARD.nickName AS nickName ,BOARD.subject AS subject ,BOARD.contents AS contents ,BOARD.hits AS hits ,BOARD.createDatetime AS createDatetime ,BOARD.createUser AS createUser ,BOARD.updateDatetime AS updateDatetime ,BOARD.updateUser AS updateUser FROM( SELECT @rownum := @rownum+1 AS rnum ,BOARD.UUID AS uuid ,BOARD.BOARD_CODE AS boardCode ,BOARD.USER_ID AS userId ,MEMBER.USERNAME AS userName ,MEMBER.EMAIL AS email ,MEMBER.NICKNAME AS nickName ,BOARD.SUBJECT AS subject ,BOARD.CONTENTS AS contents ,BOARD.HITS AS hits ,BOARD.CREATE_DATETIME AS createDatetime ,BOARD.CREATE_USER AS createUser ,BOARD.UPDATE_DATETIME AS updateDatetime ,BOARD.UPDATE_USER AS updateUser FROM BOARD LEFT OUTER JOIN MEMBER ON 1 = 1 AND BOARD.USER_ID = MEMBER.UUID ,(SELECT @rownum := 0) AS R WHERE 1 = 1
AND BOARD.BOARD_CODE = #{BOARD_CODE}
ORDER BY BOARD.CREATE_DATETIME DESC ) BOARD LIMIT #{PAGE_NUM}, #{PAGE_LIMIT}

/* kr.pe.playnote.main.dao.IBoardDao.totalCountDao */
SELECT COUNT(*) AS totalCount
FROM   BOARD 
WHERE  1 = 1

    AND BOARD_CODE = #{BOARD_CODE}
